import React from "react";
import "./App.css";

function App(){
  const [toggle, setToggle] = React.useState(false);

  const clickHandler = () => {
    setToggle(!toggle);
  }

  React.useEffect( () => {
    document.title = toggle ? "Welcome !" : "using the useEffect hook"
  });
}


export default App;
